import React from 'react';
import { Col, Row, ListGroup, } from 'react-bootstrap';

const allIcons = [
    { id: 1, iTag: "fab fa-twitter-square", a: "https://twitter.com/paulrosero33", title: "My Twitter" },
    { id: 2, iTag: "fas fa-envelope-square", a: "mailto: paul.rosero23@gmail.com", title: "My Email" },
    { id: 3, iTag: "fab fa-facebook-square", a: "https://www.facebook.com/paul.rosero.98", title: "My Facebook" }
]

const renderIcons = allIcons.map( ({ iTag, id, a, title }) => {
    // return <ListItem key={ id }>
    //     <ListItemContent id="icon">
    //         <i className={ iTag }/>
    //         <a href={ a } target="_blank" rel="noopener noreferrer" style={{ fontWeight: 'bold'}}>{title}</a>
    //     </ListItemContent>
    // </ListItem>
})

function ContactMe() {
    return (
        <Col id="contactMe-grid">
            <Cell col={ 6 }>
                <h2>Paul Rosero</h2>
                <img src="/images/ProfilePicture.png" alt="avatar" className="avatar-img" style={{ marginTop: "0px" }}/>
                <p style={{ fontWeight: "bold", margin: "1em 2em 2em 2em",padding: "1em" }}>
                    I want to thank you for taking time to visit my page. If you have any questions, ideas or want to collaborate on some work feel free to message me. I have listed my email address, Github and LinkedIn profile pages so you can reach me faster.
                </p>
            </Cell>
            <Cell col={ 6 }>
                <h3>Contact Me</h3>
                <hr style={{ borderTop: "5px dotted white", width: "85%", margin: "auto" }}/>
                <div className="icon-list">
                    <ListGroup variant="flush">
                        <ListGroup.Item action href="/">
                        Link 1
                        </ListGroup.Item>
                        <ListGroup.Item action href="/" disabled>
                        Link 2
                        </ListGroup.Item>

                    </ListGroup>,
                </div>
            </Cell>
        </Col>
    )
}

export default ContactMe;
